void mainImage( out vec4 O, vec2 U ){

    vec2 r = iResolution.xy;
    vec2 uv = U/r;


    O = vec4(smoothstep(1./r.y, 0., abs(texture(iChannel0, vec2(uv.x, 0)).x-uv.y)));

    // Smoothstep is an intentional design choice to give randomized brightness to particles
}

const uint k = 1103515245U;
vec3 hash( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}

const int p = 5;
vec3 nhash( uvec3 x ){
    vec3 t = vec3(0);
    for(int i = p; i-->0;){
        t += hash(x + uvec3(i));
    }
    t /= float(p);
    return t;
}

void mainImage( out vec4 O, vec2 U ){
    if (U.y < 1.){
        vec2 r = iResolution.xy;
        if (iFrame < 5){
            O = vec4(0);
            O.x = hash(uvec3(U.x, iDate.w, iDate.z)).x;
        } else {
            O = texture(iChannel0, U/iResolution.xy);

            // Single thread. Do whatever you want, then set c1 and c2
            // Sort here


            vec3 h = hash(uvec3(iFrame, iTime, -iFrame));
            int c = int(h.x * r.x);

            int c2 = (c+1)%int(r.x);                        
            if (h.z > .5) c2 = int(h.y * r.x);



            // No coding after here if you're simulating single thread.




            if (c2 < c){ c ^= c2; c2 ^= c; c^= c2; }// C2 always bigger
            int x = int(U.x);
            if (x==c){
                float n = texelFetch(iChannel0, ivec2(c2, 0), 0).x;
                if ( n < O.x) O.x = n;
            }

            if (x==c2){
                float n = texelFetch(iChannel0, ivec2(c, 0), 0).x;
                if (n > O.x) O.x = n;
            }
        }
    } else {
        O = vec4(0);
    }
}
