#define res iResolution
#define PI 3.14159265
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;

    vec2 sq1 = uv - vec2(res.x / res.y * 0.25, 0.5);
    vec2 sq2 = uv - vec2(res.x / res.y * 0.75, 0.5);

    vec3 col;

    if( uv.x < res.x/res.y * 0.5 )
    {col = vec3(atan(sq1.y/sq1.x) / PI + 0.5);}
    if( uv.x > res.x/res.y * 0.5)
    {col = vec3(atan(sq2.y,sq2.x) / PI * 0.5 + 0.5);}
    if(abs(uv.x - res.x/res.y * 0.5) < 0.005){col = vec3(1.0, 0.0, 0.0);}

    fragColor = vec4(col,1.0);
}

