#define PI atan(0., -1.)
#define TAU5 2. * PI / 5.

float sdPT(vec2 p, float radius){

    float radial = atan(p.x , -p.y);
    float repeat = mod(radial, TAU5) - TAU5 / 2.;
    repeat = abs(repeat) - TAU5;
    float star = cos(repeat) * length(p) - .13;

    vec2 q = abs(p + vec2(.1, .02)) - vec2(.03, .12);
    float  b = length(max(q, 0.)) + min(max(q.x, q.y), 0.);

    q = abs(p - vec2(-.065, .035)) - vec2(.01, .01);
    float  a = abs(length(max(q, 0.)) - .03) - .025;

    q = abs(p + vec2(-.07, .02)) - vec2(.03, .12);
    float  c = length(max(q, 0.)) + min(max(q.x, q.y), 0.);

    q = abs(p + vec2(-.07, -.07)) - vec2(.07, .03);
    float  e = length(max(q, 0.)) + min(max(q.x, q.y), 0.);

    return max(-b, max(-c, max(-e, max(-a, star))));
}



void mainImage(out vec4 O, vec2 I){
    vec2 R = iResolution.xy, 
         p = (I - .5 * R) / R.y;

    float d = sdPT(p, .4),
          s = smoothstep(3. / R.y, 0., d);

    O = vec4(.75, 0, 0, 1) + s;
}
