float createSun( vec2 uv, vec3 col ) {
    float d = length( vec2(uv.x-0.8, uv.y-0.6) )*.08;

    //d = sin(d*8. + iTime)/8.;
    //d = abs(d);
    d = pow(0.01 / d, 1.2);

    return d;
}

vec3 createLandscape3( vec2 uv, vec3 col )
{
    vec3 d = vec3(0.);
    float wave = ( sin( uv.x + sin( iTime * 0.3  ) * 9. ) * 0.2 ) -0.8;
    if ( uv.y < wave ) d = vec3(1.);
    return d;
}

vec3 createLandscape2( vec2 uv, vec3 col )
{
    vec3 d = vec3(0.);
    float wave = ( sin( uv.x + sin( iTime * 0.3  ) * 6. ) * 0.1 ) -0.3;
    if ( uv.y < wave ) d = vec3(1.);
    return d;
}

vec3 createLandscape_base( vec2 uv, vec3 col )
{
    vec3 d = vec3(0.);
    if ( 
        //uv.x > ((sin(2.)*.05)+0.5)
        uv.y < ( sin( (uv.x + sin( iTime * 0.3 )) * 2. ) * 0.1 )
    ) {
        d = vec3(1.);
    }

    //d = ((cos(col*2.)*.05)+0.5);

    // there is this sin
    // check if this y is higher or lower then the sign

    return d;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    //vec2 uv = fragCoord/iResolution.xy;
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;

    // Time varying pixel color
    vec3 col = ( 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)) ) * 0.3;

    col += createLandscape_base( uv, col ) * 0.2;
    col += createLandscape2( uv, col ) * 0.2;
    col += createLandscape3( uv, col ) * 0.2;
    col += createSun( uv, col );

    // Output to screen
    fragColor = vec4(col,1.0);
}
