// https://iquilezles.org/articles/palettes/
vec3 palette(float t) {
    vec3 a=vec3(0.788,0.788,0.688);
    vec3 b=vec3(0.000,0.048,-0.052);
    vec3 c=vec3(3.138,-0.642,1.018);
    vec3 d=vec3(2.488,0.333,0.667);
    return a+b*cos(6.28318*(c*t+d));
}

float h2(vec2 p)
{
    p  = 75.0*fract(p* 1.414213562);
    return fract(p.x*p.y*(p.x+p.y));
}

float h1(float x)
{
    x  = 75.0*fract(12.93145 + x * 1.414213562);
    return fract(x);
}

float noise(in vec2 x)
{
    vec2 p = floor(x);
    vec2 s = vec2(1.,0.);
    float a = h2(p+s.yy);
    float b = h2(p+s.xy);
    float c = h2(p+s.yx);
    float d = h2(p+s.xx);

    vec2 w = fract(x);
    vec2 u = w*w*w*(w*(w*6.0-15.0)+10.0);


    return -1.+2.*(a + (b-a)*u.x + (c-a)*u.y + (a - b - c + d)*u.x*u.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.xy;
    uv *= 12.;

    float s = iTime * h1(floor(uv.y + .5) * 10.) * 5.;
    if (mod(uv.y +.5, 2.) <= 1.) s *= -1.;

    float l = length(floor(uv.xyx + vec3(s, 0., s) - vec3(.5)));
    vec3 col = palette(l);
    col += noise((uv + vec2(s,0.) ) * 1.5) / 8.;
    col /= 1.2;
    float d = abs(fract(uv.x + s)  - .5);
    d = min(d, abs(fract(uv.y)- .5));
    if (d <= 0.1) {
        float f = smoothstep(0., 0.1, d);
        col = mix(vec3(f), col, f);
    }
    fragColor = vec4(col, 1.);
}
