void mainImage( out vec4 fragColour, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // all points within the circle will have a position smaller than radius.
    // circle is elongated due to aspect ratio, so multiply pos by aspect
    vec2 pos = uv - 0.5; // from -0.5 to 0.5
    // fix aspect ratio
    pos.x *= iResolution.x / iResolution.y;
    float blur = 0.01;
    float col = 1.-smoothstep(1., 1.-blur, length(pos) * 3.0);
    fragColour = vec4(col, col, col, 1.0);
}
