float GetLightCombination(float time) 
{
    time = mod(time, 40.);
    return .8 * pow(2., floor(time / 10.));
}

vec3 GetLightColor(float time)
{
    return vec3(sin(time), 1.-cos(time), 1.-sin(time));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    float t = iTime*.2;

    vec2 vecToCenter = vec2(.5)-uv;
    float distToCenter = length(vecToCenter);
    distToCenter *= 2.;
    float a = atan(vecToCenter.y, vecToCenter.x) + t;

    float lightCombination = GetLightCombination(t);
    float lightToggle = step(.5, fract(lightCombination*a+t));
    float wheelInner = step(.15, distToCenter);
    float wheelOuter = step(distToCenter, .8);
    float wheelLights = .005*tan(a*4.)*wheelInner*wheelOuter*lightToggle;

    vec3 color = GetLightColor(t) * wheelLights;

    fragColor = vec4(color,1.0);
}
