#define rot(a) mat2(cos(a+vec4(0,-1.57,1.57,0)))

void mainImage(out vec4 o, vec2 c)
{
    vec2 R = iResolution.xy, a, D, P = (c - R * .5) / R.x * 9.;
    o *= 0.;

    for (float w; w++ < 50.; P *= rot(.126)) 
    {
        // Color, hsv2rgb
        vec4 C = .2+ .8* clamp(abs(fract(w/50.+vec4(3,2,1,0)/3.)*6.-3.)-1., 0., 1.);
        // Offset and rotate coordinates
        D = (P + vec2(1.2,0)) * rot( iTime*2. + sin(iTime) );
        // Analytical integral solution for 0-width segment light
        a = atan((D.x + vec2(1,-1)) / D.y);
        o += C / dot(C, vec4(3, 5, 1, 0)) * (a.x - a.y) / D.y;
    }
    // Some color normalization
    o = 1.-exp(-o * .08);
}
