// -1 thanks to coyote
void mainImage( out vec4 O, vec2 I )
{
    I = (I+I-(I = iResolution.xy))/I.y;

    for (O -= O; 
         O.a++ < 18.;
         O += .5 / exp(60.
         * dot(
               I = abs(I *= 1.2*mat2(cos(.1*iTime + vec4(0,-11,11,0))))
                 - .4,
               I))
         );
}
