void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // 正規化されたピクセル座標
    vec2 uv = fragCoord / iResolution.yy;

    // 時間パラメータ
    float t = iTime;

    // FMシンセサイズ: 3つのオペレーターを追加
    vec2 op1 = sin(80.0 * 3.14 * uv.xy );
    vec2 op2 = sin(2.0 * 3.14 * (uv.xy + op1) );
    vec2 op3 = sin(2.0 * 3.14 * (uv.xy + op2) + t);
    vec2 fm = 1. + op3;

    // グラデーション色
    float blue = 0.5 + 0.25 * cos(3.14 * (uv.x + t / 4.0))+0.25 * cos(3.14 * (uv.y + t / 4.0));

    // FMとグラデーションの積
    vec3 col = vec3(fm, blue);

    // 色を出力
    fragColor = vec4(col,1.0);
}
