#define S(v)  smoothstep(ss,-ss, v )

// original from https://iquilezles.org/articles/distfunctions2d/
// + width
float sdSegment( in vec2 p, in vec2 a, in vec2 b, float width )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h) - width;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy) / iResolution.y;
    float ss = .75/iResolution.y;

    vec3 bg = vec3(0.396,0.573,0.812);
    vec3 c1 = vec3(0.024,0.059,0.333);

    vec3 col = bg;

    col = mix(col, c1, S(sdSegment(uv + vec2(0,.5), vec2(0), vec2(0,.3), .03)));
    col = mix(col, c1, S(sdSegment(uv + vec2(0,.1), vec2(0), vec2(0), .21)));

    // Thx @FabriceNeyret2
    float i = round((-uv.x + .18)/.12);
    if (i>=0.&&i<4.) col = mix(col, c1, S(sdSegment(uv + vec2(-.18 + i*.12,.1), vec2(0), vec2(0,.35), .03)));
    i = round( ( -uv.x + .12)/.12 );
    if (i>=0.&&i<4.) col = mix(col, bg, S(sdSegment(uv + vec2(-.12 + i*.12,.03), vec2(.0), vec2(0.,.3), .03)));

    /*
    for (float i=0.;i<4.;i++) {
        col = mix(col, c1, S(sdSegment(uv + vec2(-.18 + i * .12,.1), vec2(.0), vec2(0.,.35), .03)));
        col = mix(col, bg, S(sdSegment(uv + vec2(-.12 + i * .12,.03), vec2(.0), vec2(0.,.3), .03)));
    }
    */

    fragColor = vec4(col,1.0);
}
