#define SQRT_3 1.73205080757
#define ZOOM 2.0

// Colorization and output stuff here.
void mainImage( out vec4 fragColor, in vec2 fragCoord) {
    // Transform coordinates to correctly display hexagonal geometry
    vec2 coord = vec2((fragCoord.x*SQRT_3 + fragCoord.y) / 2.0, fragCoord.y) / ZOOM;    
    int val = GET(coord, ivec2(0, 0));

    fragColor.r = float(val % D) / float(D);
    val /= D;
    fragColor.g = float(val % D) / float(D);
    val /= D;
    fragColor.b = float(val % D) / float(D);
}

// Simple time-dependant noise function, for seeding CA
float noise(in vec2 co){
    int x = int(co.x * float(M));
    int y = int(co.y * float(M));
    int z = int(iDate.a);
    int w = int(fract(iDate.a) * float(M));
    int res = hash(x + hash(y + hash(z + hash(w))));

    return float(res % M);
}

// Main CA computation
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Randomize initial state on 0th frame or if mouse button is pressed
    if (iFrame <= 0 || iMouse.z > 0.0) {
        fragColor.x = noise(fragCoord/iResolution.xy);
    } else {
    	ivec2 d;

        int vals[7];
        int i = 0;
        int idx = 0;

    	for(d.y = -1; d.y <= +1; d.y++)
        	for(d.x = -1; d.x <= +1; d.x++){
                // To turn Moore neighborhood into hexagonal one we simply ignore a pair of opposite corners
                if((d.x == -1 && d.y == 1) || (d.x == 1 && d.y == -1)) continue;

                vals[i] = GET(fragCoord, d);
                i += 1;
             }

        // Sort the array using a sorting network
        swap(0,6); swap(2,3); swap(4,5);
        swap(0,2); swap(1,4); swap(3,6);
        swap(0,1); swap(2,5); swap(3,4);
        swap(1,2); swap(4,6);
        swap(2,3); swap(4,5);
        swap(1,2); swap(3,4); swap(5,6);

        idx = (vals[0] ^ vals[6]) % 7;

        fragColor.x = float(vals[idx]);
    }
}

#define swap(x, y) if (vals[x] > vals[y]) {int t = vals[y]; vals[y] = vals[x]; vals[x] = t;};
#define GET(coord, offset) int(texelFetch(iChannel0, ivec2(mod(coord + vec2(offset) + iResolution.xy, iResolution.xy)), 0).x)

#define D 256
#define M (D * D * D)

int hash(int x) {
    x++;
    x ^= x >> 17;
    x *= 0xed5ad4bb;
    x ^= x >> 11;
    x *= 0xac4c1b51;
    x ^= x >> 15;
    x *= 0x31848bab;
    x ^= x >> 14;
    return x;
}
