#define PI 3.14159265358979323846
#define e  2.71828182845904523


float displacement = .5; 
float roatateSpeed = -0.5;

float colorizationSpeed = -0.1;

float width =1.0;
float lineBlur = 1.0;


// define colors here because I currently only plan on using one palette
// also see http://dev.thi.ng/gradients/ 
vec3 a = vec3(0.0, 0.468, 0.428); 
vec3 b = vec3(1.208, -0.902, 0.628); 
vec3 c = vec3(-0.252, -0.022 ,0.018); 
vec3 d = vec3(1.978, -0.672 , -0.902); 

// cosine based palette, 4 vec3 params
// from https://iquilezles.org/articles/palettes/
vec3 palette(float t)
{
    return a + b*cos( 6.28318*(c*t+d) );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   // coords to clip space (i.e 0,0 at the center of the canvas) + aspect ratio fix 
   vec2 uv = (fragCoord * 2.0 - iResolution.xy)/iResolution.y;


   // get the distance from the new (0,0) origin 
   float uv0 = length(uv); 


   // spiral effect 
   //-------------------------------------------------------

   // get the angle around the origin
   float theta = (atan(uv.y / uv.x) / PI * 2.0 ) / 4.0;

   if (uv.x < 0.0) theta += 0.5; 

   // make the spiral
   float r = theta + roatateSpeed * iTime * displacement + log(uv0);
   float spiral = mod(r  / displacement, 1.0) ;

   //antialias the spiral
   spiral = smoothstep(spiral - lineBlur, spiral, width) - smoothstep(spiral, spiral + lineBlur, width); 


   //make it glow
   spiral = spiral - 0.5; 
   spiral = abs(spiral);   
   spiral = 0.08/spiral;
   vec3 col = palette( colorizationSpeed*iTime + 1.0*uv0 );

   // make it contrasty
   spiral = pow( spiral, 1.8);


   fragColor = vec4( col *  spiral, 1.0); 

}
