vec4 rectangle(vec2 uv, vec2 pos, float width, float height, vec3 color) {
	float t = 0.;
	if (uv.x >= pos.x && (uv.x < pos.x + width)
		&& (iResolution.y - uv.y) >= pos.y && ((iResolution.y - uv.y) < pos.y + height)) {
		t = 1.;
	}
	return vec4(color, t);
}

vec4 disc(vec2 uv, vec2 center, float radius, vec3 color) {
    float t = 0.;
    if (length(center-uv) < radius) {
        t = 1.;
    }
    return vec4(color, t);    
}

vec4 radial(vec2 uv, vec2 center, float r1, vec3 c1, float r2, vec3 c2, float r3, vec3 c3) {
    vec4 c = vec4(0.);
    float l = length(center-uv);
    if (l < r1) {
        c = vec4(c1,1.);
    } else if (l < r2) {
        c = vec4(c2,1.);
    } else if (l < r3) {
        c = vec4(c3,1.);
    }
    return c;
}

vec4 shape1(vec2 uv, vec2 center, float radius, vec3 color, bool invert) {
    float t = 0.;
    if (length(center-uv) < radius ||
        (uv.x > center.x - radius && uv.x < center.x + radius &&
         ((invert && uv.y < center.y && uv.y > center.y - radius) ||
          (!invert && uv.y > center.y && uv.y < center.y + radius))) ) {
        t = 1.;
    }
    return vec4(color, t);    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy;
    vec2 center = vec2(iResolution.x * .5, iResolution.y * .5);

	// colors
    vec3 purple = vec3(0.384,0.188,0.427);
    vec3 yellow = vec3(0.969,0.925,0.49);

    float size = iResolution.x * .1;

    // objects   
    vec4 s1 = shape1(uv, center + vec2(-size * 2., -size), size, yellow, false);
    vec4 s2 = shape1(uv, center + vec2(size * 2., -size), size, yellow, false);
    vec4 s3 = shape1(uv, center + vec2(0., size), size, yellow, true);

    // mix background & objects
    vec4 bg = vec4(purple,1.0);
    fragColor = mix(bg, s1, s1.a);
    fragColor = mix(fragColor, s2, s2.a);
    fragColor = mix(fragColor, s3, s3.a);

}
