   #define S(a,b)   smoothstep( a, b + .001,       U.y )
// #define S(a,b)   smoothstep( a, b + .001, fract(U.y) )

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U =  4.*u  / R.y;
    int i = int(U.y);
    float x = U.x-1.;

    O.r =       i == 3 ? S( x    , x+1. )    // what it does on OpenGL
              : i == 2 ? S( x+1. , x+1. ) 
              : i == 1 ? S( x+1. , x    )        // ‚Üê these 2 are strange.
              :          S( x+1. , x+1. ) ;

    O.g =       i == 3 ? S( x++  , x    )    // the test
              : i == 2 ? S( ++x  , x    ) 
              : i == 1 ? S(  x   , x++  ) 
              :          S(  x   , ++x  )  ;

   O.b = float( O.r==O.g );                  // check: blue/white if identical

   if ( int(u.x)%int(R.y/4.) == 0 ) O = vec4(1,0,0,0); // red separators
}
