void mainImage(out vec4 O, in vec2 U)
{
    U /= iResolution.xy;
    float t = iTime, b;

    vec2 d = vec2((((texture(iChannel1, U).r) - .5) * 2.)) * vec2(sin(t), cos(t)) * .01, lava = vec2(1., .1 * sin(t + U.x));

    b = texture(iChannel1, U + d).r;

    U *= 4.0;

    O = texture(iChannel0, U + d) * b + b * .1;

    lava = mix(vec2(1., .1 * sin(t + U.x)), vec2(1.), texture(iChannel0, U + d * .5 - t * .01).r);

    O.rg = mix(lava, O.rg, min(1., b * 3.));
}
