#define SAMPLE 16
#define PI 3.14159265359

vec4 getColorFromTexture(sampler2D _texture,vec2 uv){
	vec4 textureColor = texture(_texture, uv);  
	vec4 color=vec4(1,1,1,1);
	color *= textureColor;
	return color;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 v_uv0 = fragCoord/iResolution.xy;
    vec2 textureSize = vec2(1280.0, 720.0);
    float radius = 50.0;

    vec4 color = vec4(0.0);
	color += texture(iChannel0, v_uv0 + textureSize.xy * vec2(1.,0.)*radius);
	color += texture(iChannel0, v_uv0 + textureSize.xy * vec2(-1.,0.)*radius);
	color += texture(iChannel0, v_uv0 + textureSize.xy * vec2(0.,1.)*radius);
	color += texture(iChannel0, v_uv0 + textureSize.xy * vec2(0.,-1.)*radius);
	color += texture(iChannel0, v_uv0 + textureSize.xy * vec2(1.,1.)*radius);
	color += texture(iChannel0, v_uv0 + textureSize.xy * vec2(-1.,1.)*radius);
	color += texture(iChannel0, v_uv0 + textureSize.xy * vec2(1.,-1.)*radius);
	color += texture(iChannel0, v_uv0 + textureSize.xy * vec2(-1.,-1.)*radius);


    fragColor = color/9.;
}
