float get_scaling_factor(float y, float horizon) {
    return 1.0 / (horizon - y);
}

vec2 rotate(vec2 v, float a, vec2 around) {
    v -= around;
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, s, -s, c);
	return around + (m * v);
}

float map(float value, float min1, float max1, float min2, float max2) {
  return min2 + (value - min1) * (max2 - min2) / (max1 - min1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    float horizon = 0.1;

    // Above half screen, paint blue
    vec3 finalColor = vec3(0.1, 0.1, 0.1);

    // Below, perspective
    if (uv.y < horizon) {
        float sf = get_scaling_factor(uv.y, horizon);
        vec2 new_uv = uv * sf;
        new_uv.y *= 10.0;
        new_uv.y += iTime * 2.0;
        new_uv.x += cos(iTime) * 1.0;
        new_uv = rotate(new_uv, sin(iTime) * 0.1, vec2(cos(iTime) * 1.0, iTime * 2.0));
        new_uv = abs(mod(new_uv, 1.0) - 0.5) * 2.0;
        new_uv = 1.0/new_uv/100.0;

        // Compute color
        float colorFactor = textureLod(iChannel0, vec2(new_uv.y,0.75), 0.0 ).x;
        float cf = max(new_uv.x, new_uv.y);
        vec3 baseColor = vec3(0.8, 0.0, 0.8);
        finalColor = mix(cf * baseColor, cf * vec3(0.0, 0.0, 0.8), colorFactor) * 8.0;
    }
    else {
        float deformFactor = 1.0 / textureLod(iChannel0, vec2(uv.y / 10.0 + 0.01,0.25), 0.0 ).x;
        deformFactor = smoothstep(0.0, 1.4, deformFactor);
        uv.x *= deformFactor;
        float centerDist = length(uv - vec2(0.0, 0.1));
        float circle = 1.0 - smoothstep(0.60, 0.61, centerDist);
        float noSun = step(-0.5, sin(pow(uv.y, 1.5) * 100.0 + iTime * 3.0));
        circle *= noSun;
        vec3 sunColor = mix(vec3(0.8, 0.8, 0.0), vec3(0.8, 0.2, 0.0), 1.0 - map(uv.y, -0.2, 0.46, 0.0, 1.0));
        finalColor = (circle * sunColor) + ((1.0 - circle) * vec3(0.2, 0.0, 0.7 * (1.0 - uv.y)));
    }

    fragColor = vec4(finalColor, 1.0);
}
