void mainImage( out vec4 O, vec2 u )
{
    float t = .3*iTime, s;
    vec2  R = iResolution.xy, U;
    s = 14./R.y;// * exp2(5.*(iMouse.y/R.y-.5));
    U = s * u;                                               // scaling
    U += vec2( 1.7*sin(t+.3*U.y) -    cos(2.3*t-.2*U.x),     // distortion
               cos(1.2*t-.4*U.y) - .4*sin(2.7*t+.3*U.x) );

    float a0 = // .6,  // bubble can totally close           // target yellow cell surface. 
                  .5,  // max bubble opening
           a = s*s / determinant(  mat2(dFdx(U),dFdy(U)) ),  // surface in s*fragCoord units. 
           l = length( fract(U)-.5 ),                        // polar coord per tile cell
        // r = sqrt(max(0.,a-a0))/1.77;                      // a - pi.r^2 = a0 → r = sqrt( (a-a0)/pi )
           r = sqrt(max(0.,1.-a0/a))/1.77;                   // a*(1 - pi.r^2) = a0 → r = sqrt( (1-a0/a)/pi )

    O = vec4( smoothstep(-1.,1., (l-r)/fwidth(l)   ) );      // draw yellow material 
 // O *= .6 / a;                                                                      // debug: intensity = contraction
    if ( r==0. ) O *= vec4(1,0,0,0);                                                  // debug: negative area
    U = fract(U)-.5; l = U.x*U.y; O.b = smoothstep(1.,0., abs(l)/min(.1,fwidth(l)) ); // debug: show grid
}
