void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;
    float time = iTime * 0.5;

    float frequency = 5.0;
    float r = sin(frequency * uv.x + time) * 0.5 + 0.5;
    float g = sin(frequency * uv.y + time) * 0.5 + 0.5;
    float b = sin(frequency * (uv.x + uv.y) + time) * 0.5 + 0.5;
    vec3 iridescent = vec3(r, g, b);

    fragColor = vec4(iridescent, 1.0);
}
