void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord)/iResolution.xy;

    vec2 mouse = iMouse.xy;

    vec2 dif = fragCoord-mouse;

    float dist = length(dif);

    vec2 tang = vec2(-dif.y+dif.x, dif.x+dif.y)/10.0f;

    float angle = atan(dif.y/dif.x) + iTime*1.0f;

    if (dist > 50.0f + sin(angle*8.0f)*5.0f) {
        fragColor = texture(iChannel1, uv);
        return;
    }

    fragColor = mix(texture(iChannel0, (fragCoord + tang)/iResolution.xy), vec4(0, 0, 0, 0), 0.01);
}
