#define SHOW_INTEGRAL 0


//The derivative of the domain in the range A-B IS the
//colour of the pixel, so when you form a pixel colour
//over a larger area, you're basically solving the integrated
//area gradient over that pixel space dxdy.
float F(float radius, float dist) {
    return radius - abs(radius - dist);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    //Centered uv set
    vec2 uv = (-0.5 * iResolution.xy + fragCoord);
    uv /= iResolution.y;

    //The pixel domain width, could also specify a pixel width
    float w = max(abs(dFdx(uv.x)), abs(dFdy(uv.y))) * 2.0;
    //The radius in UV space of the circle
    float radius = 0.2 * sin(iTime * 0.1) + 0.2;

    //Integrating a line across the euclidean distance from the center
    float x = length(uv);

    //Calculate domain across A->B w.r.t the euclidean distance
    //May not need to /2 but I cant remember
    float A = x - w * 0.5;
    float B = x + w * 0.5;

    //Fundamental theorem of calculus F'(x+dx)-F'(x-dx) / dx
#if !SHOW_INTEGRAL
    float dom = (F(radius, B) - F(radius, A)) / w;
#else
    //2x just to illustrate the magnitude changing better
    float dom = 2.0 * F(radius, x);
#endif
    fragColor = vec4(dom);
}
