vec3 hsv2rgb(vec3 hsv) {
    vec3 rgb = clamp(abs(mod(hsv.x*6.0+vec3(0.0,4.0,2.0),
                              6.0)-3.0)-1.0, 0.0, 1.0);
    rgb = rgb * rgb * (3.0 - 2.0 * rgb);
    return hsv.z * mix(vec3(1.0), rgb, hsv.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 position = (fragCoord.xy / iResolution.xy) - vec2(0.5, 0.5);
    float ar = iResolution.x/iResolution.y;
    vec2 mousePos = iMouse.xy/iResolution.xy;
    float r = length(position * vec2(ar, 1.));

    //Looney Toons Colors
    vec3 orange1 = vec3(208./255.0, 86./255.0, 36./255.0);
    vec3 orange2 = vec3(107./255.0, 11./255.0, 24./255.0);
    vec3 blue1 = vec3(0.0/255.0, 26.0/255.0, 91.0/255.0);
    vec3 blue2 = vec3(3./255.0, 6.0/255.0, 29.0/255.0);

    // Light Color Generation
    float time = iTime * 0.000003;
    float hue = fract(sin(time) * 43758.5453123);
    vec3 lightColor = hsv2rgb(vec3(hue, 1.0, 1.0));


    vec3 color = vec3(0.0);


    // Lightening
    vec2 lightPos = vec2(mousePos.x-0.5, mousePos.y-0.5);
    float dist = distance(vec2(ar,1.)*position, lightPos);
    float light = smoothstep(0.5, 0.001, dist);
    color += lightColor * light;

    // Looney Toons Disks
    if (r < 0.20) { 
        color += mix(blue1,blue2, r/0.20);
    } else if (r < 0.30) { 
        color += mix(orange1,orange2,r/0.30);
    } else if (r < 0.45) { 
        color += mix(orange1,orange2,r/0.45);
    } else if (r < 0.65) { 
        color += mix(orange1,orange2,r/0.65);
    }else if (r < 0.90) {
        color += mix(orange1, orange2, r/0.90);
    } else if (r < 1.2) {
        color += mix(orange1,orange2, r/1.2);
    }
    else { 
        color += vec3(0.0);
    }

    fragColor = vec4(color, 1.0);
}
