float Interpolate(float x)
{
    return 2.0 / radians(180.0) * (sqrt(x * (1.0 - x)) * (2.0 * x - 1.0) + asin(sqrt(x)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 ar = vec2(iResolution.y / iResolution.x, 1.0);

    // Time varying pixel color
    vec3 col;

    float n = 20.0;
    vec2 gridCoord = floor(uv * n / ar) / n * ar;
    vec2 fracCoord = fract(uv * n / ar);
    vec3 offset    = vec3(1.0 / n * ar, 0.0);

    vec3 bl = textureLod(iChannel0, gridCoord            , 0.0).rgb;
    vec3 br = textureLod(iChannel0, gridCoord + offset.xz, 0.0).rgb;
    vec3 tl = textureLod(iChannel0, gridCoord + offset.zy, 0.0).rgb;
    vec3 tr = textureLod(iChannel0, gridCoord + offset.xy, 0.0).rgb;

    if (sin(iTime * 0.4) * 0.5 + 0.5 < uv.x)
        col = mix(mix(bl, br, Interpolate(fracCoord.x)), mix(tl, tr, Interpolate(fracCoord.x)), Interpolate(fracCoord.y));
    else
        col = mix(mix(bl, br, fracCoord.x), mix(tl, tr, fracCoord.x), fracCoord.y);

    col *= step(0.001, abs(uv.x - sin(iTime * 0.4) * 0.5 - 0.5));

    // Output to screen
    fragColor = vec4(col,1.0);
}
