void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = fragCoord*(vec2(1200.0/iResolution.x, 675.0/iResolution.y));

    int v = 23659;
    v *= int(coord.x)/14;
    v += (iFrame*2)/3;
    v *= int(coord.y)/13;
    v *= int(2.0*sin(coord.y));

    // LCG constants from https://en.wikipedia.org/wiki/Linear_congruential_generator
    float vf = intBitsToFloat(0x3F000000 | ((1664525*v + 1013904223) >> 9));

    float w = abs(mix(sin(4.0*iTime + coord.x/32.0), sin(6.0*iTime + coord.x/64.0), 0.4));

    fragColor = vec4(0.2 + 0.1*sin(2.0*fragCoord.x), 0.4, 1.0, 1.0)*vec4(vf);
    fragColor += (vec4(vf > 0.7 ? 1.0 : 0.0) + 0.1*vec4(vf > 0.68 ? 1.0 : 0.0))*0.5*w;
}
