#ifdef GL_ES
precision mediump float;
#endif

float rand(float seed,vec2 st){
    float f=1000.*dot(st,vec2(.520,.350));
    return fract(sin(f)*seed);
}
float draw(float rand1,float rand2,vec2 st){
    float gap=15./iResolution.y,pct;
    pct=fract((rand1<.5)?st.x:-st.x)+floor(st.y);
    vec2 center=(rand1<.5)?
        vec2( 0 , rand2-.33 > .5 ):
        vec2(1.,rand2-.33<.5);
    if(rand2<.33){
        return smoothstep(gap,0.,abs(pct-st.y));
    }
    else if(rand2>.33 && rand2<.66){
        return (rand2<.5)?(smoothstep(pct-gap,pct,st.y)):smoothstep(0.,gap,pct-st.y);
    }
    else if(rand2>.66){
        return smoothstep(0.,gap,1.-length(center-fract(st)))-smoothstep(0.,gap,.5-length(center-fract(st)));
    }
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = fragCoord.xy/iResolution.y,center,m;
    st*=10.;
    vec3 color = vec3(0.);
    color=mix(color,vec3(1.),draw(rand(4243.176,floor(st)),rand(4246.416,floor(st)),st));
    fragColor = vec4(color,1.);
}
