void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    float size = 1.1;
    float bias = 0.65;

    vec2 t = vec2((fragCoord.x / iResolution.x - 0.5) * 2.0, (fragCoord.y / iResolution.y - 0.5) * 2.0);

    t *= bias;

    float d = sqrt(t.x*t.x + t.y*t.y);

    float i = max(size-(d*d*d), 0.0);

    fragColor = vec4(texture(iChannel0, fragCoord/iResolution.xy).xyz * i, 1.0);
}
