float sdf_seg_2d(in vec2 s, in vec2 e, in vec2 pos)
{
    vec2 se = e - s;
    vec2 sp = pos - s;
    float scale = clamp( dot(sp, se) / dot(se, se), 0.0, 1.0);

    return length(sp - se * scale);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    //vec2 uv = fragCoord/iResolution.xy;

    vec3 color = vec3(0.0);

    float ani = 1.0 - max(0.0, sin(iTime));

    vec2 start = vec2(-0.75 * ani, 0.0);
    vec2 end = vec2(0.75 * ani, 0.0);
    vec2 p =  (fragCoord * 2.0 - iResolution.xy)/min(iResolution.x, iResolution.y);

    //float smooth_range = 4.0 /  min(iResolution.x, iResolution.y);
    float smooth_range = 2.0 * length(vec2(dFdx(p.x), dFdy(p.y)));
    //框
    {
        float v = sdf_seg_2d(start, end, p);
        v = smoothstep(0.5, 0.5 + smooth_range, v) - smoothstep(0.6, 0.6 + smooth_range, v);
        color += vec3(v);
    }

    {
        float sp = 1.0 - min(1.0, length(p - start) / 0.02);
        sp = pow(sp, 0.5);
        color += vec3(0.0, sp, 0.0);
    }

    {
        float ep = 1.0 - min(1.0, length(p - end) / 0.02);
        ep = pow(ep, 0.5);
        color += vec3(ep, 0.0, 0.0);
    }

    // Output to screen
    fragColor = vec4(color,1.0);
}
