const int N = 10;
vec2 PList[N];

vec3 palette( float t ) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(0.8, 0.8, 0.918);
    vec3 d = vec3(1.38840734641021,1.59840734641021,1.74840734641021);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float E = 0.0;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    for (int i=0; i<N; i++) {
        PList[i] = fract(vec2(sqrt(2.0),sqrt(3.0))*float(i)) + (fract(vec2(sqrt(5.0),sqrt(7.0))*float(i))*2.0-1.0)*float(iTime)/50.0;
        float d = length(PList[i]-uv);
        E += 1.0/float(N)*cos(20.0*d+1.3*iTime+sqrt(11.0)*float(i))*exp(-0.001*d);
    }

    E = pow(E,0.3);
    //E = smoothstep(0.1, 0.3, E);

    fragColor = vec4(palette(E), 1.0);
}
