void mainImage( out vec4 C, vec2 U )
{
    vec3  c, rgb;
    vec2  R = iResolution.xy,
          u, g;
    float s = 27.,            // scale
          t = iTime/60.,      // sec per int
          z = (iMouse.y/R.y); // zoom
    if (z < .1) z = 0.;       // snap to 0 when close

    //t = 1./4.; // manual entry

    u = U/R.y*s/(1.-z);         // screen coords
    g = abs(fract(u+.5)-.5)/.5; // cell grid
    c += min(g.x, g.y) * mod(ceil(u.x) * ceil(u.y) * t, 1.); // shift with time

    C = vec4(c*.5+c*c, 1);
}
