const float pi =3.14159265;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 screen_pos = (2.0 * fragCoord.xy - iResolution.xy)/iResolution.y;

    float x=screen_pos.x;
    float y=screen_pos.y;

    float t=0.5-0.5*sin(iTime);

    float a=mix(1.0,-2.0,t);
    float b=mix(-1.0,1.0,t);
    float n=mix(1.0,7.0,t);
    float m=mix(2.0,4.6,t);

    float s = a*sin(pi*n*x)*sin(pi*m*y)+b*sin(pi*m*x)*sin(pi*n*y);
    float c = step(abs(s), 0.1);

    vec3 col = vec3(c);
    // Output to screen
    fragColor = vec4(col,1.0);
}
