void mainImage( out vec4 O, in vec2 SC ) 
{ 
    O = TEXF0(SC);

#define EXP (O.z*1.2) // looks good
#define R ( pow(O.x, EXP) )
#define G ( pow(O.y, EXP) )
#define B ( pow(O.z, EXP) )

    O = vec4(R, G, B, 1.);
}

#define B 1.005
#define MAX 1000.
#define STEPS 4000

float A;

int test(vec2 x)
{
    int i = 0; float sq = MAX*MAX;
    while (i < STEPS && dot(x, x) <= sq) 
    {
        x = vec2(1. - A * x.x * x.x + x.y, B * x.x);
        i++;
    }
    return i;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 12. * (fragCoord / iResolution.xy) - vec2(5.5, 5.5);
    float v = iTime / 60.;
    A = abs(v - floor(v + 0.5));

    float t = float(test(uv))/float(STEPS);

    vec3 col = vec3(t, t*t*t*t, max(0., sin(0.4*6.28317 * t)));
    fragColor = vec4(col, t);
}

void mainImage( out vec4 O, in vec2 SC )
{
    float t = 0.91;

    O = 
        iFrame == 0 ?
            vec4( 0 ) :
            TEXF0(SC) * (1. - t) + TEXF1(SC) * t;
}
