const float dens = .05; // density of the grid in y

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y; // 1:1 aspect ratio, y over (0-1)
    vec2 m = iMouse.xy/iResolution.y;

    vec2 offset = m-uv;
    float t = iTime/2.;
    float lens = pow(1. - clamp(length(offset), 0., 1.), 9. + 2.*sin(t));

    vec2 coord = uv + offset*lens;


    vec2 grid = mod(coord.xy/dens, 1.)-.5;
    float dots = smoothstep(1., 0., pow(length(grid), .3));

    float wave = cos(t-length(offset)-.6);
    vec3 col = vec3(pow(dots, 2.2))*vec3(1. - 10.*wave, 2. , 3.0 + 2.*wave);
    fragColor = vec4(col,1.0);
}
