vec2 rotate(in vec2 p,float angle){
  float sina=sin(angle);
  float cosa=cos(angle);
  return vec2(cosa*p.x-sina*p.y,sina*p.x+cosa*p.y);
}
float sdBox( in vec2 p, in vec2 rad )
{
    p = abs(p)-rad;
   // Omits corners, so outer field lines are not correct.
    //return max(p.x, p.y); 
    // Considers corners also.
    return min(max(p.x, p.y), 0.) + length(max(p, 0.));
}
float sdBoxRoate( in vec2 p, in vec2 rad ,in float angle)
{
  //rotate the coord by time
  p=rotate(p,angle);

   // p = abs(p)-rad;

    //没太懂为什么要除以这个数
   //return max(p.x,p.y)/max(abs(cosa+sina), abs(cosα-sinα));
  // return max(p.x,p.y)/max(abs(cosa+sina),abs(cosa-sina));



    // Render.
    // Rotation shouldn't affect size, so the same
    // radius should be fine.
    return sdBox(p, rad); 
}

bool isbox( in vec2 p, in vec2 rad )
{
    p = abs(p)-rad;
    return p.x<=0.0&&p.y<=0.0;
}
