const float pi = 3.1415926573;
const float b1 = 0.6/6.; // Section between bands
const float b2 = 1.5/6.;
const float b3 = 2.1/6.;
const float b4 = 3.4/6.;
const float b5 = 4.4/6.;
const float sq = sqrt(7.);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    //uv.y += mod(uv.x + iTime/5.,1.);
    uv.y += .08*sin(uv.x*pi*1. + 3.71*iTime) + 
            .02*cos(uv.x*pi*3.1 + 2.3*iTime);
    // Time varying pixel color
    vec3 col = vec3(0.,0.,0.);
    col.r = 0.8 + 
            0.1*smoothstep(1.-b1,1.-b2, uv.y) + 
            0.1*smoothstep(1.-b2,1.-b3, uv.y) -
            0.6*smoothstep(1.-b3,1.-b4, uv.y) -
            0.15*smoothstep(1.-b4,1.-b5, uv.y)+
            0.5*smoothstep(1.-b5,1.-1., uv.y);

    col.g = 0.2 + 
            0.2*smoothstep(1.-b1,1.-b2, uv.y) + 
            0.5*smoothstep(1.-b2,1.-b3, uv.y) -
            0.2*smoothstep(1.-b3,1.-b4, uv.y) -
            0.2*smoothstep(1.-b4,1.-b5, uv.y) -
            0.7*smoothstep(1.-b5,1.-1., uv.y);

    col.b = 0.2 + 
            0.0*smoothstep(1.-b1,1.-b2, uv.y) + 
            0.1*smoothstep(1.-b2,1.-b3, uv.y) +
            0.0*smoothstep(1.-b3,1.-b4, uv.y) +
            0.4*smoothstep(1.-b4,1.-b5, uv.y)+
            0.3*smoothstep(1.-b4,1.-1., uv.y);


    //col += vec3(1.-pow(cos(uv.y*pi + 3.*pi/2.),9.),
    //            sin(uv.y*pi),
    //            1.+cos(uv.y*pi));



//col += vec3(smoothstep(0.4,0.6,uv.y+.3),smoothstep(0.4,0.6,uv.y+.3),0.);

    // Output to screen
    fragColor = vec4(col,1.0);
}
