float hash3(vec2 xy){
    xy = mod(xy, .19);
    float h = dot(xy.yyx,vec3(.013, 27.15, 2027.3));
    h *= h;
    h *= fract(h);

    return fract(h);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = fragCoord/iResolution.xy;

    vec2 off = 1. / iResolution.xy;

    vec2 right = vec2(off.x, 0.);
    vec2 up = vec2(0., off.y);

    vec2 rF = vec2(1., 0.);
    vec2 uF = vec2(0., 1.);



    vec4 col = texture(iChannel0, uv);

    vec4 c1 = texture(iChannel0, uv);
    vec4 c2 = texture(iChannel0, uv + right);
    vec4 c3 = texture(iChannel0, uv + up);



    uv += (.5 * rF * length(c1 - c2)) + (.5 * uF * length(c1 - c3));

    col = texture(iChannel0, uv);

    fragColor = col;
}
