float hash3(vec2 xy){
    xy = mod(xy, .19);
    float h = dot(xy.yyx,vec3(.013, 27.15, 2027.3));
    h *= h;
    h *= fract(h);

    return fract(h);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = fragCoord/iResolution.xy;
    vec2 uvc = (uv - .5) * 2.;

    mat2 r1;
    r1[0] = vec2(1., cos(iTime));
    r1[1] = vec2(sin(iTime) * uvc.x, 1.);


    uv = ((r1 * uvc) / 2.) + .5;




    vec4 col = vec4(0.);

    vec4 c1 = texture(iChannel0, uv);


    mat4 rot;
    rot[0] = vec4(sin(iTime), 0., 0., 0.);
    rot[1] = vec4(0., sin(iTime - 2.5), 0., 0.);
    rot[2] = vec4(0., 0., cos(iTime), 0.);
    rot[3] = vec4(0., 0., 0., 1.);

    col = rot * c1 + .2;



    fragColor = col;
}
