void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 vec2 uv = (fragCoord * 2.0 - iResolution.xy)/iResolution.y ;

    float c = 16.;
    float x = uv.x;
    float y = uv.y;
    //uv= vec2(mod(c,x),mod(c,y));
    //uv= vec2(mod(x,c),mod(y,c));

    //float d = uv.x * uv.y;
    float d = tan(x*c) * sin(y* c) * 3.;
    //float d = length(uv);


    float r = (-1. + d * 2. + 4. * sin(iTime + 1.5));
    r = smoothstep(0.2, 0.3, r);

    float g = (-1. + d * 2.2 + 4. * sin(iTime - 0.7));
    g = smoothstep(0.2, 0.5, g);

    float b = (-1. + d * 2. + 2. * sin(iTime + 0.01));
    b = smoothstep(0.2, 0.3, b);



    vec3 col = vec3(pow(0.08/r, 1.5),pow(0.08/g, 0.6),0.);

    //col = cross(col, vec3(1.,1.,1.));


    fragColor = vec4(col,1.0);
}
