void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float flowSpeed=3.0;
    float mx = max(iResolution.x, iResolution.y);
    vec2 st = gl_FragCoord.xy/mx;
    vec3 color = vec3(0.0);
    float dist =(sin(iTime*flowSpeed +
    		distance(st, iMouse.xy/mx))) +0.90;
    vec2 bl = step(vec2(0.01)*dist,mod(st,vec2(0.055)));
    float pct = bl.x * bl.y;

    color = vec3(pct);
    fragColor = vec4(color,1.0);
}
