void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord.xy - 0.5*iResolution.xy)/iResolution.y*10.;
    uv.x -= (iMouse.x - 0.5*iResolution.x)/iResolution.y*10.;
    uv.y -= (iMouse.y - 0.5*iResolution.y)/iResolution.y*10.;
    float i = 0.;
    if(dot(uv, uv) < 100.)
        i = 1./dot(uv, uv);
    // Output to screen
    fragColor = vec4(i, i, i, 1.);
}
