void mainImage( out vec4 fragColor, in vec2 fragCoord ) //argument is fragmentposition, returns fragmentcolor(red/1, green/1, blue/1, alpha/1)
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 col = texture(iChannel0, uv.xy-vec2(1.0/iResolution.x * iTime * 64.0, 0.0));
    float size = 18.0;
    float steps = 16.0;
    for (float i = 0.0; i < steps; i+=1.0)
    {
        for (float j = 0.0; j < size; j+=1.0)
        {
            col += texture(iChannel0, uv.xy-vec2((1.0/iResolution.x)*j*cos((360.0/steps)*i)), (1.0/iResolution.y)*j*sin((360.0/steps)*i));
        }
    }
    col /= size*steps;
    fragColor = col;
}
