/*
Author: Felipe Tovar-Henao [www.felipe-tovar-henao.com]
Title: Anaglitch
*/


#define PI 3.14159265359
#define TWO_PI 6.28318530718

vec2 adjustViewport(vec2 coord,vec2 res){
	return(coord*2.-res)/(res.x<res.y?res.x:res.y);
}

float fold(in float x){
    return abs(mod(x+1.,2.)-1.);
}

vec2 fold(in vec2 p){
    return vec2(fold(p.x),fold(p.y));
}

mat2 rot2(in float a){
    float c=cos(a);
    float s=sin(a);
    return mat2(c,-s,s,c);
}

float cosine(in float x,in float s){
    float y=cos(fract(x)*PI);
    return floor(x)+.5-(.5*pow(abs(y),1./s)*sign(y));
}


float _noise(in float x){
	return.5+.5*sin(floor(x)*4321.+iTime);
}
float noise(in float x){
	return _noise(x-_noise(x));
}

float getColor(in vec2 uv){
	float d=length(uv);
	d=.25/d;
	d=pow(d,3.+length(uv));
	d=cosine(d,2.5);
	return d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv=adjustViewport(fragCoord.xy,iResolution.xy);
	float t=iTime*.25;
	float nt=noise(t);
	uv*=1.5+nt;
	float x=uv.y;
	float y=uv.x;
	float g=dot(uv,uv);
	float ts=t*.1;
	for(float i=0.;i<6.;i++){
		float tmp=x;
		float scl=pow(1.9,i);
		tmp=noise(x+scl);
		x=noise(y-scl);
		y=tmp;
		y=fract(y-x+ts);
		x=fract(x-y-ts);
		vec2 sh=vec2(y,x)*rot2(scl+t);
		x=sh.x;
		y=sh.y;
	}
	float z=noise(uv.y*4.+nt*.4);
	uv=fold(uv)+vec2(x,y);

	float red=getColor(uv);
	uv.x+=sin(t+z)*.05;
	float cyan=getColor(uv);

	fragColor=vec4(vec3(red,cyan,cyan),1.);
}
