void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord / iResolution.xy) - 0.5;
	uv.x *= iResolution.x / iResolution.y; // ratio fix

	vec3 color = vec3(0.1, 0.8, 2.);

	float d = length(uv);

	d = sin(d * 14. + iTime);

	d = abs(d + 0.7);

	d = 0.05 / d;

	d += 0.06;

	color *= d;

	fragColor = vec4(color, 1);
}
