mat2 rotate2D(float rad){
    return mat2(cos(rad), sin(rad), -sin(rad), cos(rad));
}

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.2,e,g=mod(iTime*.3,3.); vec3 p;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e);
    p=vec3(log(length(p)),exp(p.z)-g,atan(p.x, p.y));
    p.z-=4.;
    p.xz*=rotate2D(g);

    for(j=0.;j++<16.;)
        p=abs(abs(p)-.1)-.01,
        d-=dot(p,p.zyx)/2e4;

    e+=d;
}o+=vec4(-log(p/4.),1)*3./i;
}
