vec3 palette(float t ) {

    vec3 a = vec3(0.580,0.366,0.076);
    vec3 b = vec3(0.943,0.049,0.314);
    vec3 c = vec3(1.365,1.409,0.465);
    vec3 d = vec3(5.803,4.520,0.224);

    return a+b*cos(6.283118*(c*t*d) );
}

//Shape from https://iquilezles.org/articles/distfunctions2d/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;

    vec3 finalcolor = vec3(0.0);

    uv = fract(uv * 2.0)-0.5;


    float sd = length(uv);

    vec3 color = palette(length(uv0) + iTime);

    sd = sin(sd*8. + iTime)/8.;
    sd = abs(sd);

    sd = 0.009 / sd;

    finalcolor += color * sd;


    fragColor = vec4(finalcolor, 1.);
}
