void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 center = vec2(0.5, 0.5);
    vec2 direction = uv - center;

    float angle = atan(direction.y, direction.x);
    float distance = length(direction);

    float numPetals = 8.0;
    float rotationSpeed = iTime * 0.5;
    float petalSize = 0.1 + 0.05 * sin(iTime * 2.0);
    float petalFactor = cos(angle * numPetals + rotationSpeed) * petalSize;

    // Kaleidoscope effect
    angle = mod(angle * numPetals / 2.0, 3.14159 / numPetals) * 2.0 * numPetals;

    float scale = 0.5 + sin(iTime * 0.5) * 0.5;
    float colorFactor = distance * scale - petalFactor;
    vec3 color = vec3(
        0.9 + 0.1 * sin(colorFactor * 10.0 + iTime),
        0.9 + 0.1 * sin(colorFactor * 20.0 + iTime * 1.5),
        0.9 + 0.1 * sin(colorFactor * 30.0 + iTime * 2.0)
    );

    // Prism-like effect
    vec3 prism = vec3(
        sin(colorFactor * 10.0 + iTime),
        sin(colorFactor * 20.0 + iTime * 1.5),
        sin(colorFactor * 30.0 + iTime * 2.0)
    );
    color += prism * 0.1;

    fragColor = vec4(color, 1.0);
}
