#define n_tau 6.2831
float f_n_dist_reg_polygon(vec2 o, float n_vertices) {
  float n_radians = atan(o.x, o.y);
  float n_radians_per_corner = n_tau / (n_vertices);

  return cos(floor(.5+n_radians/n_radians_per_corner)*n_radians_per_corner-n_radians)*length(o);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 o_trn = (fragCoord.xy - iResolution.xy *.5) / iResolution.y;

    float n = f_n_dist_reg_polygon(
        o_trn,
        5.
    );

    fragColor = vec4(n);
}
