vec2 hash2(float a){
    vec2 b = vec2(a*a, a - 0.34);
    b.x *= (float(int(47.*b.x) | int(36.*b.y)) + a) / 1e4;
    b.y *= (float(int(95.*b.x) | int(14.*b.y)) + a) / 1e4;
    b.x = mod(b.x,100.);
    b.y = mod(b.y,100.);
    return b;
}// do hash(hash2(seed), seed); for 1d noise



float hash(vec2 a, float seed){
    vec2 b = a;

    b += 10.;
    b *= (3.*b.x + 2.) * (2.*b.y + 9.) / 9.;
    b.x *= b.y;
    b.y *= (float(int(38.*b.x) | int(83.*b.y)) + mod(474.*seed*b.x,10000.)) / 1e8;
    b.y = mod(b.y, 1000.);
    b.x *= (float(int(77.*b.x) | int(23.*b.y)) + mod(723.*seed*b.y,10000.)) / 1e8;
    b.x = mod(b.x, 1000.);

    return fract(b.x * b.y);
}

float oneDnoise(float a, float seed){
    return hash(hash2(a),seed);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    // Output to screen
    fragColor = vec4(vec3(hash(uv, iTime)),1.0);
}
