const float speed = 1.0;

const float frequency = 2.0;

const float waveAmplitude = 1.0;

const float uampmul = 1.0;

vec4 sineWave(vec4 pt, vec2 pos)
{

        float offsetX = sin(pt.y * frequency + iTime * speed);
        float offsetY = sin(pt.x * (frequency * 2.0) - (iTime / 2.0) * speed);
        float offsetZ = sin(pt.z * (frequency / 2.0) + (iTime / 3.0) * speed);
        pt.x = mix(pt.x, sin(pt.x / 2.0 * pt.y + (5.0 * offsetX) * pt.z), waveAmplitude * uampmul);
        pt.y = mix(pt.y, sin(pt.y / 3.0 * pt.z + (2.0 * offsetZ) - pt.x), waveAmplitude * uampmul);
        pt.z = mix(pt.z, sin(pt.z / 6.0 * (pt.x * offsetY) - (50.0 * offsetZ) * (pt.z * offsetX)), waveAmplitude * uampmul);


    return vec4(pt.x, pt.y, pt.z, pt.w);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;
    vec4 pt = texture(iChannel0, uv);
    fragColor = sineWave(pt, uv);
}
