// -52 thanks to Fabrice🍓
void mainImage( out vec4 o, vec2 u )
{
    u = .2*u + vec2(7, 16) * iTime;
    int j;   
    for (float n = 3., i ; i < n; i++) 
        j ^= int(dot(u, cos(3.14*i/n + vec2(0,11))));

    o *= 0.;
    o[abs(j-(j&36)) % 11]++;
}


